import { useState, useEffect } from "react"
import React from "react"
const API = process.env.REACT_APP_API;


export const Users = () => {

    const [name, setName]= useState("")
    const [description, setDescription]= useState('')
    const [award, setAward]= useState('')
    const [dialog, setDialog]= useState('')

    const[contests, setContests]=useState([])


    const handleSubmit = async (e) => {      
        const res = await fetch(`${API}/create_contest_post/`, {
            method:'POST',
            headers:{
                'Conten-Type': 'application/json'
            },
            body: JSON.stringify(
                {
                    name,
                    description,
                    award,
                    dialog
                }
            )
        })
        const data = await res.json();
        console.log(data)
        
        
    }
    const getUsers = async () => {
        const res = await fetch(`${API}/home`)
        const data = await res.json();
        setContests(data)

    }
   
    useEffect(() =>{
        getUsers();
    }, [])

    const deleteContest = async (id) =>{
        const res = await fetch(`${API}/delete_contest/<id>`,{
            method:'DELETE'

        })
        const data=res.json();
        console.log(data)
    }
    const editContest = (id) =>{
        console.log(id)
    }

    return(
        <div className="row">
            <div className="col-md-4">
                <form onSubmit={handleSubmit} className= "card card-body">
                    <div className="form-group">
                        <input 
                        type="text" 
                        onChange={e=> setName(e.target.value)}
                        value={name}
                        className='form-control'
                        placeholder="Nombre del Evento"
                        
                        />
                    </div>
                    <div className="form-group">
                        <input 
                        type="text" 
                        onChange={e=> setDescription(e.target.value)}
                        value={description}
                        className='form-control'
                        placeholder="Description"
                        
                        />
                    </div>
                    <div className="form-group">
                        <input 
                        type="text" 
                        onChange={e=> setAward(e.target.value)}
                        value={award}
                        className='form-control'
                        placeholder="Award"
                        
                        />
                    </div>
                    <div className="form-group">
                        <input 
                        type="text" 
                        onChange={e=> setDialog(e.target.value)}
                        value={dialog}
                        className='form-control'
                        placeholder="dialog"
                        />
                    </div>
                    
                    <button className="btn btn-primary btn-block">
                        apply
                    </button>
                </form>
            </div>
            <div className="col-md-4">
                <table className="table  table-striped">
                    <thead>
                    <tr >
                        <th>NAME</th>
                        <th>Description</th>
                        <th>Award</th>
                        <th>dialog</th>
                        <th>Operator</th>
                    </tr>
                    </thead>
                    <tbody>
                        {contests.map(contest =>(
                            <tr key={contest.id_contest}>
                                <td>${contest.contest_name}</td>
                                <td>${contest.desciption}</td>
                                <td>${contest.award}</td>
                                <td>${contest.dialog}</td>
                                <td>
                                <button className="btn btn-secondary btn-sm btn-block"
                                className="btn btn-danger btn-sm btn-block"
                                onClick={(e)=>editContest(contest.id_contest)}
                                >
                                    Editar
                                </button>
                                <button 
                                className="btn btn-danger btn-sm btn-block"
                                onClick={(e)=>deleteContest(contest.id_contest)}
                                >
                                    Eliminar
                                </button>
                                </td>
                                
                        </tr>
                        ))}
                </tbody>
                </table>
            </div>
            

        </div>


    )
    }
